require 'spec_helper'

describe 'sshkeys_restruct_to_hash' do

  context 'correct input data' do
    it do
      should run.with_params({
          'first-test-key'  => {
            'key'  => '',
            'type' => 'ssh-rsa',
          },
          'second-test-key' => {
            'key'  => '',
            'type' => 'ssh-rsa',
          },
        },
        'sometestuser',
        'some.fqdn'
      ).and_return({
        'first-test-key_at_sometestuser@some.fqdn' => {
          'key_name' => 'first-test-key',
          'user'     => 'sometestuser',
          'type'     => 'ssh-rsa',
          'key'      => '',
        },
        'second-test-key_at_sometestuser@some.fqdn' => {
          'key_name' => 'second-test-key',
          'user'     => 'sometestuser',
          'type'     => 'ssh-rsa',
          'key'      => '',
        },
      })
    end
  end

  context 'wrong hash structure' do
    it do
      #be carefull with output here in rspec-puppet =<1.0.1 https://github.com/rodjek/rspec-puppet/issues/138
      should run.with_params({
          'first-test-key'  => {
            'key'  => '',
            'type' => 'ssh-rsa',
          },
          'second-test-key' => {
            'type' => 'ssh-rsa',
          },
        },
        'sometestuser',
        'some.fqdn'
      ).and_raise_error(/wrong structure of input hash/)

      #be carefull with output here in rspec-puppet =<1.0.1 https://github.com/rodjek/rspec-puppet/issues/138
      should run.with_params({
          'first-test-key'  => {
            'key'  => '',
            'type' => 'ssh-rsa',
          },
          'second-test-key' => {
            'key'      => '',
          },
        },
        'sometestuser',
        'some.fqdn'
      ).and_raise_error(/wrong structure of input hash/)

      #be carefull with output here in rspec-puppet =<1.0.1 https://github.com/rodjek/rspec-puppet/issues/138
      should run.with_params({
          'first-test-key'  => {
            'key'  => '',
            'type' => 'ssh-rsa',
          },
          'second-test-key' => 'broken value',
        },
        'sometestuser',
        'some.fqdn'
      ).and_raise_error(/wrong structure of input hash/)
    end
  end

  context 'wrong count or args' do
    it do
      #be carefull with output here in rspec-puppet =<1.0.1 https://github.com/rodjek/rspec-puppet/issues/138
      should run.with_params('sometestuser','some.fqdn').and_raise_error(/wrong number of arguments given/)
    end
  end

  context 'wrong type instead of hash' do
    it do
      #be carefull with output here in rspec-puppet =<1.0.1 https://github.com/rodjek/rspec-puppet/issues/138
      should run.with_params('test string','sometestuser','some.fqdn').and_raise_error(/first argument should be a hash/)
    end
  end
  

end
